import { observer } from "mobx-react-lite";
import { FC, useRef, useState, MouseEventHandler } from "react";
import store from "../../store/store";
import { UserIcon } from "../Header/styles";
import { SideBarContainer } from "./styles";

export const SideBar:FC = observer(()=>{
    const overLayRef = useRef<HTMLDivElement>(null);

    const handleOverlayClick: MouseEventHandler<HTMLDivElement> = ({
      target,
    }) => {
      if (target !== overLayRef.current) store.setIsSideBar(false);
    };
    return (
        <SideBarContainer ref={overLayRef} onClick={handleOverlayClick} visible={store.isSideBar} >
            <UserIcon/>
            {store.user.email}
        </SideBarContainer>
    )
})
