import styled from "styled-components";
import user from "./../../utils/user.png";

import icon1 from "./../../../utils/icon1.png";
import icon2 from "./../../../utils/icon2.png";
import icon3 from "./../../../utils/icon3.png";
import icon4 from "./../../../utils/icon4.png";
import icon5 from "./../../../utils/icon5.png";
import icon6 from "./../../../utils/icon6.png";


type Props = {icon: string}

export const UserIcon = styled.div<Props>`
  height: 40px;
  width: 40px;
  background: url(${(props) => props.icon ? props.icon : user});
  border-radius: 50%;
  background-position: center center;
  background-repeat: no-repeat;
  background-size: cover;
  margin-right: 10px;
  transition: box-shadow 0.3s ease;
  cursor: pointer;
  :hover {
    box-shadow: -3px -3px 15px 3px ${(props) => props.theme.main.color};
  }
`;
